package com.google.ar.sceneform.samples.ARFurniture;

import android.content.Intent;
import android.os.Bundle;
import android.view.View;
import android.widget.Button;

import androidx.appcompat.app.AppCompatActivity;

public class GoodInfoActivity extends AppCompatActivity {
    static int num = (int)(Math.random()*10000);
    static int no = (int)(Math.random()*3);
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_good_info);
        //浸入ar展示界面
        Button arView = (Button) findViewById(R.id.arView);
        arView.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                Intent intent = new Intent(GoodInfoActivity.this, GltfActivity.class);
                startActivity(intent);
            }
        });
    }
    public void addToCart(View view){
        //测试一下加入商品
        String str = "沙发"+ num;
        String shopName = "家具专卖"+no;
        CarResponse.OrderDataBean.CartlistBean good = new CarResponse.OrderDataBean.CartlistBean();
        good.setShopName(shopName);
        good.setProductName(str);
        good.setCount(1);
        good.setChecked(true);
        good.setPrice(1000);
        good.setSize("1.8米");
        good.setColor("蓝");
        good.setDefaultPic("data:image/jpeg;base64,");
        ShoppingCartActivity cart = new ShoppingCartActivity();
        cart.addToCart(good);
    }
}